# Download package pycryptodome if you don't have it
from Crypto.Util.number import bytes_to_long, getPrime

flag = b"vgucypher{fake_flag}"
BITS = 512
E_BITS = 15
pt = bytes_to_long(flag)

p = getPrime(BITS)
q = getPrime(BITS)
n = p*q

e_1 = getPrime(E_BITS)
e_2 = getPrime(E_BITS)

ct_1 = pow(pt, e_1, n)
ct_2 = pow(pt, e_2, n)
print("ct_1 = ", ct_1)
print("ct_2 = ", ct_2)

print("e_1 = ", e_1)
print("e_2 = ", e_2)

print("n = ", n)

